import React, { Component } from 'react';
import './App.css';
import web3 from './web3';
import ecommerce from './ecommerce';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {manager: '',
                productNumber: '',
                reviewNumber: '',
                users: ''
                };
  }
  async componentDidMount(){
    const manager = await ecommerce.methods.manager().call();
    const productNumber = await ecommerce.methods.productNumber().call();
    const reviewNumber = await ecommerce.methods.reviewNumber().call();
    const users = await ecommerce.methods.users().call();

    this.setState({ manager, productNumber, reviewNumber, users});

  }
  render () { 
    console.log(ecommerce);

    return (
      <div>
        <h2> Blockchain-based product review platform </h2>
        <hr/>
        <p> Contract is deployed by {this.state.manager} </p>
        <hr/>
        <p> There are {this.state.productNumber} products available for users to write reviews </p>
        <hr/>
        <p> There are a total of {this.state.reviewNumber} reviews available on the platform </p>
        <hr/>
        <p> There are a total of {this.state.users} users who contributed to the platform </p>
        <hr/>
      </div>
    );
  }
}

export default App;
