import React, { Component } from 'react';
import './App.css';
import web3 from './web3';
import ecommerce from './ecommerce';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {productNumber: ''};
  }
  async componentDidMount(){
    const productNumber = await ecommerce.methods.productNumber().call();

    this.setState({ productNumber});

  }
  render () { 
    console.log(ecommerce);

    return (
      <div>
        <h2> Blockchain-based product review platform </h2>
        <p> There are {this.state.productNumber} products available for users to write reviews </p>
      </div>
    );
  }
}

export default App;
